      headers: {
        'Content-Type': 'application/json'
      }
    })
    const json = await response.json()

    if (!response.ok) {
      setError(json.error)
    }
    if (response.ok) {
      setError(null)
      setTitle('')
      setLoad('')
      setReps('')
      console.log('new workout added:', json)
    }

  }




  return (
    <div> <div className="container">
    <div className="screen">
    
      <div className="screen-body">
        <div className="screen-body-item left">
          <div className="app-title">
            <span>CONTACT</span>
            <span>US</span>
          </div>
          <div className="app-contact">CONTACT INFO : +91 9449573121</div>
        </div>
        <div className="screen-body-item">
          <div className="app-form">
            <div className="app-form-group">
              <input className="app-form-control" placeholder="NAME" defaultValue="Nandan " required />
            </div>
            <div className="app-form-group">
              <input type="email" className="app-form-control" placeholder="Email" required />
            </div>
            <div className="app-form-group">
              <input className="app-form-control" placeholder="CONTACT NO" required/>
            </div>
            <div className="app-form-group message">
              <input className="app-form-control" placeholder="MESSAGE" required />
            </div>
            <div className="app-form-group buttons">
              <button className="app-form-button">CANCEL</button>
              <button className="app-form-button">SEND</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div></div>
  )
}

export default Contactus
