import { useState } from "react";
import "./movieForm.css";

const MovieForm = () => {
  const [title, setTitle] = useState("");
  const [year, setYear] = useState("");
  const [runtime, setrunTime] = useState("");
  const [genres, setGenres] = useState("");
  const [director, setDirector] = useState("");
  const [plot, setPlot] = useState("");
  const [posterUrl, setposterUrl] = useState("");

  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let obj = JSON.parse(title);
    setTitle(obj.title);
    setYear(obj.year);
    setrunTime(obj.runtime);
    setGenres(obj.genres);
    setDirector(json.plot);
    setPlot(json.plot);
    setposterUrl(json.posterUrl);

    const movie = { title, year, runtime, genres, director, plot, posterUrl };

    const response = await fetch("/api/movies", {
      method: "POST",
      body: JSON.stringify(movie),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
    }
    if (response.ok) {
      setError(null);
      setTitle("");
      setYear("");
      setDirector("");
      setGenres("");
      setrunTime("");
      setPlot("");
      setposterUrl("");

      console.log(" New Movie added: ", json);
    }
  };

  return (
    <form className="create" onSubmit={handleSubmit}>
      <label>Movie Title:</label>
      <textarea
        className="enterJson"        
        onChange={(e) => setTitle(e.target.value)}
        value={title}
      />

      <button className="button">Add Movie</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default MovieForm;
