import React from "react";

import { useEffect, useState } from "react";
import { useMoviesContext } from "../hooks/useMoviesContext";
import { Link } from "react-router-dom";

import MovieForm from "../components/movieform";
import Modal from "../Modal/Modal";
import "./movies.css";



const Tvshow = () => {

  const [movies,setmoviess] = useState('');
   const [show, setShow] = useState(false);

  useEffect(() => {
    const fetchMovies = async () => {
      const response = await fetch("/api/tvshows");
      const json = await response.json();

      if (response.ok) {
        setmoviess(json);
        console.log(json);
      }
    };

    fetchMovies();
  }, []);



  return (
    <div id="container">
      <div id="section">
        <div id="content">
          <button className="addMovie" onClick={() => setShow(true)}>
            Add TV Show
          </button>
          <Modal title="Add A New Movie" onClose={() => setShow(false)} show={show}>
            <MovieForm onClose={() => setShow(false)} />
          </Modal>

          <div className="post">
            {/* post cards */}
            {movies &&
              movies.map((movie) => (
                <div className="postCards" key={movie._id}>
                  <a href="article.html">
                    <Link to={`/api/movies/${movie._id}`} movie={movie}>
                      <img src={movie.posterUrl} />
                      <h1 className="postTitle">{movie.title}</h1>
                      <div className="movie-info">
                        <span> {movie.year} </span>||
                        <span> {movie.runtime +  "Seasons."} </span>
                        <div>{movie.genres.join(", ")}</div>
                      </div>
                    </Link>
                  </a>
                </div>
              ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Tvshow;
