import { useState } from "react";
import "./movieForm.css";

const MovieForm = () => {
  const [jsonn,setJsonn] = useState("");
 

  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let obj = JSON.parse(jsonn);
    let title = obj.title;
    let year = obj.year;
    let runtime= obj.runtime;
    let genres = obj.genres;
    let director = obj.director;
    let plot = obj.plot;
    let posterUrl = obj.posterur

    const movie = { title, year,runtime };
    console.log(movie);

    const response = await fetch("/api/movies", {
      method: "POST",
      body: JSON.stringify(movie),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
    }
    if (response.ok) {
      setError(null);
        console.log(" New Movie added: ", json);
    }
  };

  return (
    <form className="create" onSubmit={handleSubmit}>
      <label>Movie Title:</label>
      <textarea
        className="enterJson"        
        onChange={(e) => setJsonn(e.target.value)}
        value={jsonn}
      />

      <button className="button">Add Movie</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default MovieForm;
