import React from "react";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./singleMovie.css";

const SingleMovie = () => {
  const [movie, setMovie] = useState(null);
  const [review, setReview] = useState(null);
  const { id } = useParams();

  function filterById(obj) {
    if ( obj.title === movie.title) 
    {
      return true
    } 
    return false;
  }

  useEffect(() => {
    const fetchMovie = async () => {
      const response = await fetch(`/api/movies/${id}`);
      const json = await response.json();

      if (response.ok) {
        setMovie(json["movie"]);
        let reviewFilter = json["review"].filter(review => review.title === json["movie"]["title"]);
    
        setReview(json["review"]);
      }
    };

    fetchMovie();
  }, []);
  console.log(review)

  
    

  return (
    <div className="container">
      {movie && (
        <div className="box">
          <img className="movieImage" src={movie.posterUrl} alt="" />
          <h1>
            Movie Name : <span className="titleSpan">{movie.title}</span>
          </h1>
          <div className="details">
            <span>Release: {movie.year}</span>
            <span>Runtime:{movie.runtime}</span>
          </div>
          <h3 className="plot">PLOT : <span>{movie.plot}</span> </h3>

          <h4>Reviews for the Movie : </h4>
          {review && review.map((review)=>(
            <div>
            <h2>{review.reviewGiven}</h2>
            <h3>{review.rating}</h3>

            </div>
      ))}

           
          
        </div>
      )}
    </div>
  );
};

export default SingleMovie;
