import React from "react";
import "./movies.css";
import { useEffect, useState } from "react"


const Movies = () => {

  const [movies, setMovies] = useState(null)

  useEffect(() => {
    const fetchMovies = async () => {
      const response = await fetch('/api/movies')
      const json = await response.json()

      if (response.ok) {
        setMovies(json)
      }
    }

    fetchMovies()
  }, [])

  return (
    <div id="container">
      <div id="section">
        <div id="content">
          <div className="sectionTitle">Latest Post</div>
          <div className="post">
            {/* post cards */}
            {movies && movies.map((movie) => (
              <>
              <p ></p>
              <div className="postCards">
              <a href="article.html">
                <img src="https://images.hdqwalls.com/download/disney-plus-avengers-endgame-4k-jv-1440x2960.jpg" alt="" />
                <h2 className="postTitle">
                {movie.title}
                </h2>
              </a>
            </div>
            </>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Movies;
