import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { CSSTransition } from "react-transition-group";
import "./Modal.css";

const Modal = props => {

    const [jsonn,setJsonn] = useState("");
  const [password,setPassword] = useState("");
  const [error, setError] = useState(null);



  const handleSubmit = async (e) => {
    
    e.preventDefault();
    let obj = JSON.parse(jsonn);
    if(password==="1237"){
      const response = await fetch("/api/movies", {
        method: "POST",
        body: JSON.stringify(obj),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = await response.json();
  
      if (!response.ok) {
        setError(json.error);
      }
      if (response.ok) {
        
        setError(null);
          console.log(" New Movie added: ", json);
      }
    }else{
       let  wrong = document.getElementById('passwordWrong');
       wrong.innerHTML="Password is Wrong"
       wrong.style.color = "red";

    }

    
  };





  const closeOnEscapeKeyDown = e => {
    if ((e.charCode || e.keyCode) === 27) {
      props.onClose();
    }
  };

  useEffect(() => {
    document.body.addEventListener("keydown", closeOnEscapeKeyDown);
    return function cleanup() {
      document.body.removeEventListener("keydown", closeOnEscapeKeyDown);
    };
  }, []);

  return ReactDOM.createPortal(
    <CSSTransition
      in={props.show}
      unmountOnExit
      timeout={{ enter: 0, exit: 300 }}
    >
      <div className="modal" onClick={props.onClose}>
        <div className="modal-content" onClick={e => e.stopPropagation()}>
          <div className="modal-header">
            <h4 className="modal-title">{props.title}</h4>
          </div>
          <div className="modal-body">
          <form className="create" onSubmit={handleSubmit}>
      <label>Movie Title:</label>
      <textarea
        className="enterJson"        
        onChange={(e) => setJsonn(e.target.value)}
        value={jsonn}
      />

      <label>Password :</label>
      <input 
        type="text" 
        onChange={(e) => setPassword(e.target.value)} 
        value={password}
      />
      <div id="passwordWrong"></div>

      <button className="buttonToAdd" >Add Movie</button>
      {error && <div className="error">{error}</div>}
    </form>
          </div>
          <div className="modal-footer">
            <button onClick={props.onClose} className="button">
              Close
            </button>
          </div>
        </div>
      </div>
    </CSSTransition>,
    document.getElementById("root")
  );
};

export default Modal;
